input = ''
pattern = 'GTGAATAGT'

def find_match(string, pattern)
  if match = string.index(pattern)
    @match_positions << (@match_positions.last || 0) + match + 1
    find_match string[match + 1..-1], pattern
  end
end

@match_positions = []
find_match input, pattern

puts @match_positions.join(' ')